import { useState, useEffect } from 'react'
import { useNavigate, useLocation } from 'react-router-dom'
import { Button, Row, Col, ListGroup, Image, Card } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import CheckoutSteps from '../components/CheckoutSteps'
import { createOrder } from '../actions/orderActions'

const PlaceOrderScreen = ({history}) => {

    const orderCreate = useSelector(state => state.orderCreate);
    const {order, error, success } = orderCreate;

    const dispatch = useDispatch();
    const cart = useSelector(state => state.cart);
    const navigate = useNavigate();
    const location = useLocation();

    cart.prices = [['Items',0], ['Shipping',0], ['Tax',0], ['Total',0]];
    cart.prices[0][1] = (cart.cartItems.reduce((acc,item) => acc += (item.price * item.qty),0)).toFixed(2);
    cart.prices[1][1] = (cart.prices[0][1]> 2000 ? 0 : 200).toFixed(2)
    cart.prices[2][1] = Number((0.05) * (cart.prices[0][1])).toFixed(2);
    cart.prices[3][1] = cart.prices.reduce((acc,item) => acc += Number(item[1]),0).toFixed(2)

    useEffect(() => {
        if(success){
            navigate(`/order/${order._id}`);
        }

        if(!cart.paymentMethod){
            navigate('/payment');
        }
    },[success,navigate,order,cart]);

    const placeOrder = () => {
        dispatch(createOrder({
            orderItems: cart.cartItems,
            shippingAddress: cart.shippingAddress,
            paymentMethod: cart.paymentMethod,
            prices: cart.prices,

        }));
    }

    return (
        <div>
            <CheckoutSteps step1 step2 step3 step4 />

            <Row>
                <Col md={8}>
                    <ListGroup variant='flush'>
                        <ListGroup.Item>
                            <h2>Shipping</h2>

                            <p>
                                <strong>Shipping: </strong>
                                {cart.shippingAddress.address}, {cart.shippingAddress.city}
                                {' '}
                                {cart.shippingAddress.postalCode},
                                {' '}
                                {cart.shippingAddress.country}
                            </p>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <h2>Payment Method</h2>

                            <p>
                                <strong>Method: </strong>
                                {cart.paymentMethod}  
                            </p>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <h2>Order Items</h2>
                            { cart.cartItems.length === 0 ? 
                            (<Message variant='info'>
                                Your Cart is Empty
                            </Message>) :
                            (
                            <ListGroup variant='flush'>
                                {cart.cartItems.map((item,index) => (
                                    <ListGroup.Item id={index} key={index} variant='fluid'>
                                        <Row>
                                            <Col md={1}>
                                              <Image src={item.image} alt={item.name} fluid rounded/>
                                            </Col>

                                            <Col>
                                               <Link to={`/product/${item.product}`}  style={{textDecoration: 'none'}}>
                                                {item.name} 
                                               </Link>
                                            </Col>

                                            <Col md={4}>
                                                {item.qty} X Rs.{item.price} = Rs.{(item.price * item.qty ).toFixed(2) }
                                            </Col>

                                        </Row>
                                    </ListGroup.Item>
                                ))}
                            </ListGroup>
                            )          
                            }
                        </ListGroup.Item>
                    </ListGroup>
                </Col>

                <Col md={4}>
                    <Card>
                        <ListGroup variant='flush'>
                            <ListGroup.Item>
                                <h2>Order Summary </h2>
                            </ListGroup.Item>

                            {cart.prices.map((items,index)=> (
                                <ListGroup.Item id={index} key={index}>
                                    <Row>
                                        <Col>{items[0]} : </Col>
                                        <Col>Rs.{items[1]}</Col>
                                    </Row>
                                </ListGroup.Item>
                            ))}

                            <ListGroup.Item>
                                {error && <Message variant='danger'>{error}</Message>}
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Button
                                type='button'
                                className='w-100'
                                disabled={cart.cartItems.length===0}
                                onClick={placeOrder}
                                >
                                    Place Order
                                </Button>
                            </ListGroup.Item>
                        </ListGroup>
                    </Card>
                </Col>
            </Row>
        </div>
    );
}

export default PlaceOrderScreen;
