import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { Row, Col, ListGroup, Image, Card } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import { getOrderDetails, payOrder } from '../actions/orderActions'
import { ORDER_PAY_RESET } from '../constants/orderConstants'
import { PayPalButton } from 'react-paypal-button-v2'

const OrderScreen = () => {
    const { id:orderId } = useParams();
    const dispatch = useDispatch();

    const orderDetails = useSelector(state => state.orderDetails);
    const {order, error, loading } = orderDetails;

    const orderPay = useSelector(state => state.orderPay);
    const {success:successPay, loading:loadingPay } = orderPay;

    const [sdkReady, setSdkReady ] = useState(false);

    if(!loading && !error){
        order.itemsPrice = order.orderItems.reduce((acc, item) => acc + item.price * item.qty, 0).toFixed(2)
    }

    // AUL8yaFvzpClfgI4CnmIJw4Emrk0hPTFoZpZa-_RDyilmWm7U6aoSVcvT776QHYTXPCPFJTYUXU5zp-I

    const addPayPalScript = () => {
        const script = document.createElement('script');
        script.type = 'text/javascript';
        script.source = 'https://www.paypal.com/sdk/js?client-id=AUL8yaFvzpClfgI4CnmIJw4Emrk0hPTFoZpZa-_RDyilmWm7U6aoSVcvT776QHYTXPCPFJTYUXU5zp-I&currency=INR';
        script.async = true;
        script.onload = () => {
            setSdkReady(true);
        }
        document.body.appendChild(script);
    }

    useEffect(() => {
        if(!order || successPay || order._id !== Number(orderId)){
            dispatch({type:ORDER_PAY_RESET});
            dispatch(getOrderDetails(orderId));
        }else if(!order.isPaid){
            if(!window.paypal){
                addPayPalScript();
            }else{
                setSdkReady(true);
            }
        }
    },[order,orderId,dispatch,successPay]);

    const successPaymentHandler = (paymentResult) => {
        dispatch(payOrder(orderId, paymentResult))
    }

    return (loading?(
        <Loader />
    ): error ? (
        <Message variant='danger'>{error}</Message>
    ):(
        <div>
            <h1>Order : {order._id}</h1>
            <Row>
                <Col md={8}>
                    <ListGroup variant='flush'>
                        <ListGroup.Item>
                            <h2>Shipping</h2>
                            <p><strong>Name: </strong>{order.user.name}</p>
                            <p>
                                <strong>Email: </strong>
                                <a href={`mailto:${order.user.email}`} 
                                   style={{ textDecoration: 'none' }}
                                   >
                                    {order.user.email}
                                </a>
                            </p>
                            <p>
                                <strong>Shipping: </strong>
                                {order.shippingAddress.address},  {order.shippingAddress.city}
                                {'  '}
                                {order.shippingAddress.postalCode},
                                {'  '}
                                {order.shippingAddress.country}
                            </p>

                            {order.isDelivered ? 
                                (<Message variant='success'>Delivered on {order.deliveredAt}</Message>):
                                (
                                    <Message variant='warning'>Not Delivered</Message>
                                )
                                }
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <h2>Payment Method</h2>
                            <p>
                                <strong>Method: </strong>
                                {order.paymentMethod}
                            </p>

                            <p>
                                {order.isPaid ? 
                                (<Message variant='success'>Paid on {order.paidAt} </Message>):
                                (
                                    <Message variant='warning'>Not Paid</Message>
                                )
                                }
                            </p>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <h2>Order Items</h2>
                            {order.orderItems.length === 0 ? <Message variant='info'>
                                Order is empty
                            </Message> : (
                                    <ListGroup variant='flush'>
                                        {order.orderItems.map((item, index) => (
                                            <ListGroup.Item key={index}>
                                                <Row>
                                                    <Col md={1}>
                                                        <Image src={item.image} alt={item.name} fluid rounded />
                                                    </Col>

                                                    <Col>
                                                        <Link to={`/product/${item.product}`} style={{ textDecoration: 'none' }}>{item.name}</Link>
                                                    </Col>

                                                    <Col md={4}>
                                                        {item.qty} X Rs. {item.price} = <br/>Rs. {(item.qty * item.price).toFixed(2)}
                                                    </Col>
                                                </Row>
                                            </ListGroup.Item>
                                        ))}
                                    </ListGroup>
                                )}
                        </ListGroup.Item>

                    </ListGroup>

                </Col>

                <Col md={4}>
                    <Card>
                        <ListGroup variant='flush'>
                            <ListGroup.Item>
                                <h2>Order Summary</h2>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Items:</Col>
                                    <Col>Rs. {order.itemsPrice}</Col>
                                </Row>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Shipping:</Col>
                                    <Col>Rs. {order.shippingPrice}</Col>
                                </Row>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Tax:</Col>
                                    <Col>Rs. {order.taxPrice}</Col>
                                </Row>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Total:</Col>
                                    <Col>Rs. {order.totalPrice}</Col>
                                </Row>
                            </ListGroup.Item>

                            {!order.isPaid && (
                                <ListGroup.Item>
                                    {loadingPay && <Loader /> }
                                    {sdkReady ? (<Loader />):(
                                        <PayPalButton 
                                          amount={order.totalPrice}
                                          onSuccess={successPaymentHandler}
                                        />
                                    )}
                                </ListGroup.Item>
                            )}

                        </ListGroup>
                    </Card>
                </Col>
            </Row>
        </div>
    ));
}

export default OrderScreen
