import Spinner from 'react-bootstrap/Spinner';
const Loader = () => {
    return (  
        <Spinner
        animation='border'
        role='status'
        style={{
            height:'100px',
            width:'100px',
            margin:'auto',
            display:'block'
        }}
        >
            <span className='sr-only'></span>
        </Spinner>
    );
}

export default Loader;
