import { useState, useEffect } from 'react'
import { Link, useNavigate, useParams } from 'react-router-dom'
import {  Form, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Loader from '../components/Loader'
import Message from '../components/Message'
import { FormContainer } from '../components/FormContainer' 
import { getUserDetails, updateUser } from '../actions/userActions'
import { USER_UPDATE_RESET } from '../constants/userConstants'

const UserEditScreen = () => {

    const {id:userId} = useParams();

    const [name, setName ] = useState('');
    const [email, setEmail ] = useState('');
    const [isAdmin, setIsAdmin ] = useState(false);

    const dispatch = useDispatch();

    const userDetails = useSelector(state => state.userDetails)
    const { error, loading, user } = userDetails;

    const userUpdate = useSelector(state => state.userUpdate)
    const { error:errorUpdate , loading: errorLoading, success: successUpdate } = userUpdate;

    const navigate = useNavigate();

    useEffect(() => {

        if(successUpdate){
            dispatch({type:USER_UPDATE_RESET});
            navigate('/admin/userlist/');
        } else {

            if(!user || !user.name || user._id !== Number(userId)){
                dispatch(getUserDetails(userId))
            }else{
                setName(user.name)
                setEmail(user.email)
                setIsAdmin(user.isAdmin)
            }
        }
    },[user,userId,dispatch,successUpdate,navigate]);

    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(updateUser({_id:user._id, name, email, isAdmin }));
    }

    return (
    <div>
        <Link to='/admin/userlist'>
           Go Back
        </Link>


        <FormContainer>
            <h1>Edit User</h1>
            {loadingUpdate && <Loader />}
            {errorUpdate && <Message variant='danger'></Message>}

            {loading ? <Loader />: error ? (<Message variant='danger'>{error}</Message>):(
                <Form onSubmit={submitHandler}>
                    <Form.Group controlId='name' className='my-2'>
                        <Form.Label>Name</Form.Label>
                        <Form.Control
                            type='name'
                            placeholder='Enter your Name'
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                        ></Form.Control>
                    </Form.Group>

                    <Form.Group controlId='email' className='my-2'>
                        <Form.Label>Email Address</Form.Label>
                        <Form.Control
                            type='email'
                            placeholder='Enter Email'
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        ></Form.Control>
                    </Form.Group>

                    <Form.Group controlId='isadmin'>
                        <Form.Check
                            type='checkbox'
                            label='Is Admin'
                            checked={isAdmin}
                            onChange={(e) => setIsAdmin(e.target.checked)}
                        ></Form.Check>
                    </Form.Group>

                    <br />

                    <Button type='submit' variant='primary'>
                        Update
                    </Button>
                </Form>
            )}
        </FormContainer>
    </div>
    );
}

export default UserEditScreen;
